INSERT INTO `pt_posts` (`id`, `title`, `description`, `category`, `image`, `text`, `tags`, `time`, `user_id`, `active`, `views`, `shared`) VALUES
(1, 'میكس پله‌ی ئێستا به‌رده‌سته‌', 'میكس پله‌ی یه‌كه‌مین تۆڕی ڤیدیۆی كوردی ئێستا به‌رده‌سته‌ بۆ به‌كارهێنان !', '8', 'upload/photos/2019/03/dLsYCZocQ6L7OVMArqR4_20_6c2896d0a51469d119d05b50f7dad5f2_image.png', '&lt;div id=&quot;js_4&quot; class=&quot;_5pbx userContent _3576&quot; data-ft=&quot;{&amp;quot;tn&amp;quot;:&amp;quot;K&amp;quot;}&quot;&gt;&lt;div class=&quot;_5wj-&quot; dir=&quot;rtl&quot;&gt;&lt;p&gt;میكس پله‌ی ئێستا به‌رده‌سته‌&lt;/p&gt;&lt;p&gt;میكس پله‌ی یه‌كه‌مین تۆڕی ڤیدیۆی كوردی ئێستا به‌رده‌سته‌ بۆ به‌كارهێنان !&lt;/p&gt;&lt;p&gt;میكس پله‌ی چیه‌ ؟&amp;nbsp;&lt;br&gt;میكس پله‌ی یه‌كه‌مین تۆڕی ڤیدیۆیی كوردیه‌ له‌سه‌ر ئاستی كوردستان هاوشێوه‌ی Youtube و Twitch و پلاتفۆڕمه‌ ڤیدیۆیه‌ جیهانیه‌كان به‌ زمانی شیرینی كوردی و تایبه‌تمه‌ندی ناوازه‌ ئێستا به‌رده‌سته‌ بۆ به‌كارهێنان...&lt;/p&gt;&lt;p&gt;تایبه‌تمه‌ندیه‌كانی میكس پله‌ی چین ؟&lt;/p&gt;&lt;p&gt;بڵاوكردنه‌وه‌ی ڤیدیۆ تاكو قه‌باره‌ی 10 گێگابایت.فرۆشتنی ڤیدیۆ به‌ ئۆنڵاین&lt;br&gt;به‌خشین (به‌خشین به‌ كه‌ناڵه‌كان Donate)&amp;nbsp;&lt;br&gt;به‌ده‌ست هێنانی پاره‌ له‌ ڕیگای بڵاوكردنه‌وه‌ی ڕێكلام له‌سه‌ر ڤیدیۆكانت.&lt;br&gt;ئاسان بۆ به‌كارهێنان و خێرا&lt;br&gt;هێمای سه‌لماندن&lt;br&gt;پارێزراو&lt;br&gt;هه‌بوونی پاڵپشتی به‌ زمانی كوردی و زیاتر له‌ 5 زمان.&lt;br&gt;وه‌رگرتنه‌وه‌ی پاره‌ به‌ كاش و بانك&lt;br&gt;هێنانی ڤیدیۆ له‌ تۆڕه‌كانی تری ڤیدیۆی به‌ ئاسانترین شێوه‌ به‌بێ به‌رزكردنه‌وه‌&lt;br&gt;كه‌ناڵه‌ پڕبینه‌ره‌كان ئۆفه‌ری تایبه‌ت ده‌یناگرێته‌وه‌&lt;br&gt;ئازادی به‌كارهێنان&lt;/p&gt;&lt;p&gt;ئه‌پڵیكه‌یشنی میكس پله‌ی بۆ ئه‌ندرۆید&amp;nbsp;&lt;br&gt;&lt;a href=&quot;https://play.google.com/store/apps/details?id=com.mixplay.Android&amp;amp;fbclid=IwAR0zD41eHsLVUI7TfX9qV7dZqq9ZvIYZ74Fmgy8ZdiYHGsFPd3CIIRRitKY&quot; target=&quot;_blank&quot; rel=&quot;noopener nofollow&quot; data-ft=&quot;{&amp;quot;tn&amp;quot;:&amp;quot;-U&amp;quot;}&quot; data-lynx-mode=&quot;async&quot; data-lynx-uri=&quot;https://l.facebook.com/l.php?u=https%3A%2F%2Fplay.google.com%2Fstore%2Fapps%2Fdetails%3Fid%3Dcom.mixplay.Android%26fbclid%